package forPattern;

public class Pattern12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k =0;
		for(int i = 1;i<=5;i++) {
			k = 6;
	    	  for(int j = 1; j<=i;j++) {
	    		
	    		  k--;
	    		  System.out.print(k+" ");
	    	  }
	    	  System.out.println();
	      }
	}

}
