//Write a Java program to print all alphabets from a to z. - using while loop
package whileloop;

import java.util.Scanner;

public class AtoZ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch='a';
		while(ch<='z'){
			System.out.println(ch);
			ch++;
		}
	}

}
